const pushItemToModal = (content, title) => {

    return {
        type: 'PUSH_MODAL_ITEM',
        payload: { title, content }
    }
}

const removeModalItem = (tabKey) => {

    return {
        type: 'REMOVE_MODAL_ITEM',
        payload: { tabKey }
    }
}

export {
    pushItemToModal,
    removeModalItem,
}