import React from 'react'
import {Link} from 'react-router-dom'
import TableCell from './table-cell';

const TableRow = (props) => {

  if (typeof props.linkTo !== "undefined") {
    return (
      <Link
        to={props.linkTo}
        className={typeof props.className == "undefined" ? "grid" : `grid ${props.className}`}
        style={{gridTemplateColumns: props['grid-template-columns']}}
      >
        {React.Children.map(props.children, (cell, index) => {
          if (!cell) return;
          return <TableCell {...cell.props} />;
        })}

      </Link>
    )
  } else {
    return (
      <div
        className={typeof props.className == "undefined" ? "grid" : `grid ${props.className}`}
        style={{gridTemplateColumns: props['grid-template-columns']}}
      >
        {React.Children.map(props.children, (cell, index) => {
          if (!cell) return;
          return <TableCell {...cell.props} />;
        })}
      </div>
    );
  }

}

export default TableRow