import React from 'react';

const InputTimeRange = () => {
  return (
    <div className="form_item">
      {label}
      <div className="form_item__input_wrap">
        <input
          type={inputType}
          placeholder={props.placeholder}
          id={htmlFor}
          name={props.name}
          value={value}
          disabled={disabled}
          onChange={e => {
            let value = e.target.value;

            if (typeof props.mask == "function") {
              value = props.mask(value);
            }
            props.formContext.setFieldValue(props.name, value);
          }}
          className={props.class || 'form_item__text'}
          autoComplete="off"
        />
      </div>
    </div>
  );
};

export default InputTimeRange;