import React from 'react'

class StripedDropLink extends React.Component {

    
    render() {
        return(
            <div
                style={this.props.style || {}}
                className="striped-drop-menu__list__item"
                onClick={(e) => {
                    if (typeof this.props.originalElement.props.onClick == "function") {
                        this.props.originalElement.props.onClick(e);
                    }
                }}
            >
                {this.props.originalElement.props.children}
            </div>
        )
    }
}

export default StripedDropLink