import React from "react";
import InputFileDefault from "./input-file-default";
import FileInputCustom from "./file-input-custom";

const FileInput = (props) => {

  const onSelect = (e) => {
    props.onSelect(e);
  }

  let button = (props.children) ?
    <FileInputCustom onSelect={onSelect} name={props.name}>{props.children}</FileInputCustom>
    : <InputFileDefault onSelect={props.onSelect} {...props}/>;

  return(
    <div>
      {button}
    </div>
  );
}

export default FileInput;